import{getQueryString}from"./helpers.js";const branchSelect=document.getElementById("branchSelect");function updateQueryString(e,t){const a=new URL(window.location);t?a.searchParams.set(e,t):a.searchParams.delete(e),window.location=a}function goToPage(e){updateQueryString("page",e)}branchSelect.addEventListener("change",(e=>{const t=e.target.value,a=new URL(window.location);a.searchParams.delete("page"),t?a.searchParams.set("branch",t):a.searchParams.delete("branch"),window.location=a}));const pageNum=document.querySelectorAll(".page-num"),next=document.querySelector(".page-num-next");pageNum.forEach((e=>{e.addEventListener("click",(e=>{e.preventDefault();const t=e.target.dataset.page;t&&goToPage(t)}))}));