import{getQueryString}from"./helpers.js";function updateQueryString(e,t){const n=new URL(window.location);t?n.searchParams.set(e,t):removeQueryString(e),window.location=n}function removeQueryString(e){const t=new URL(window.location.href);t.searchParams.delete(e),window.history.replaceState({},"",t)}function goToPage(e){updateQueryString("page",e)}branchSelect.addEventListener("change",(e=>{const t=e.target.value;removeQueryString("page"),t?updateQueryString("branch",t):window.location=window.location.pathname}));const pageNum=document.querySelectorAll(".page-num");pageNum.forEach((e=>{e.addEventListener("click",(e=>{e.preventDefault();const t=e.target.dataset.page;t&&goToPage(t)}))})),window.addEventListener("load",(()=>{const e=new URLSearchParams(window.location.search),t=e.get("branch")||"";e.get("page");branchSelect.value=t}));