function getMetaContent(e){return document.querySelector(`meta[name="${e}"]`)?.content||null}function capitalizeFirstLetter(e){return"string"!=typeof e||0===e.length?e:e.charAt(0).toUpperCase()+e.slice(1)}function wait(e){return new Promise((t=>{setTimeout((()=>{t(),lecturesCards.classList.remove("hidden")}),e)}))}function getQueryString(e){return new URLSearchParams(window.location.search).get(e)}function setQueryString(e,t){const n=new URL(window.location);t?n.searchParams.set(e,t):n.searchParams.delete(e)}export{getMetaContent,capitalizeFirstLetter,wait,getQueryString,setQueryString};