const timeLeft=document.querySelectorAll(".time-left");function getTimeRemaining(e,t){const[n,a,o]=e.split("-").map(Number),i=new Date(o,a-1,n),s=t.toLowerCase().includes("training"),r=s?2:5,m=s?15:14,g=new Date(i);g.setMonth(g.getMonth()+r),g.setDate(g.getDate()+m);const l=new Date;if(l.setHours(0,0,0,0),g<l)return'<p class="time-remaining">Completed</p>';let c=g.getMonth()-l.getMonth();c+=12*(g.getFullYear()-l.getFullYear());const M=new Date(l);M.setMonth(M.getMonth()+c);let h=Math.floor((g-M)/864e5);return h<0&&(c--,M.setMonth(M.getMonth()-1),h=Math.floor((g-M)/864e5)),`\n    <p class="time-remaining text-orange-600">${c} Months</p>\n    <p class="time-remaining">${h} Days</p>\n  `}document.addEventListener("DOMContentLoaded",(()=>{timeLeft.forEach((e=>{const t=e.dataset.start,n=e.dataset.groupName;e.innerHTML=getTimeRemaining(t,n)}))}));