const timeLeft=document.querySelectorAll(".time-left");function getTimeRemaining(e,t){const[n,a,o]=e.split("-").map(Number),s=new Date(o,a-1,n),i=t.toLowerCase().includes("training"),r=i?2:5,l=i?15:21,m=new Date(s);m.setMonth(m.getMonth()+r),m.setDate(m.getDate()+l);const g=new Date;if(g.setHours(0,0,0,0),m<g)return'<p class="time-remaining">Completed</p>';let c=m.getMonth()-g.getMonth();c+=12*(m.getFullYear()-g.getFullYear());const u=new Date(g);u.setMonth(u.getMonth()+c);let M=Math.floor((m-u)/864e5);return M<0&&(c--,u.setMonth(u.getMonth()-1),M=Math.floor((m-u)/864e5)),`\n    <p class="time-remaining text-orange-600 w-full">\n     <span>${c}</span> Months\n    </p>\n    <p class="time-remaining w-full">${M} Days</p>\n  `}document.addEventListener("DOMContentLoaded",()=>{timeLeft.forEach(e=>{const t=e.dataset.start,n=e.dataset.groupName;e.innerHTML=getTimeRemaining(t,n)})});