import{capitalizeFirstLetter}from"./helpers.js";const branchSelect=document.getElementById("branchSelect"),branchForm=document.getElementById("branch-form"),urlParams=new URLSearchParams(window.location.search);document.addEventListener("DOMContentLoaded",(function(){fetch("functions/Branches/get_branches.php").then((e=>e.json())).then((e=>{"success"==e.status&&(branchSelect.innerHTML='<option value="1" selected>Select a Branch</option>',e.data.forEach((e=>{let t=document.createElement("option");t.value=e.id,t.textContent=capitalizeFirstLetter(e.name),urlParams.get("branch")==e.id&&(t.selected=!0),branchSelect.appendChild(t)})))})).catch((e=>console.error("Error fetching lectures:",e)))})),branchSelect.onchange=function(){branchForm.submit()};